import React from 'react';
import { useSelector } from 'react-redux';

import { Container, Board, Square, Piece } from './styles';
import { showMovement } from '../logic';

export default function Page() {
  const board = useSelector((state) => state.board.board);

  function showMovements({ square }) {
    console.log(board);
    // return showMovement({ board, square });
  }

  return (
    <Container>
      <Board>
        {board.map((row) =>
          row.map((square) => (
            <Square key={row.indexOf(square)} color={square.squareColor}>
              {square.piece && (
                <Piece
                  key={row.indexOf(square)}
                  color={square.piece}
                  onClick={() => showMovement({ square })}
                />
              )}
            </Square>
          ))
        )}
      </Board>
    </Container>
  );
}
