import React from 'react';
import { useSelector } from 'react-redux';

import { Container, Board, Square, Piece } from './styles';

export default function Page() {
  const board = useSelector((state) => state.board.board);

  function movePiece() {}

  return (
    <Container>
      <Board>
        {board.map((row) =>
          row.map((square) => (
            <Square key={row.indexOf(square)} color={square.squareColor}>
              {square.piece && (
                <Piece key={row.indexOf(square)} color={square.pieceColor} />
              )}
            </Square>
          ))
        )}
      </Board>
    </Container>
  );
}
