import { takeLatest, all } from 'redux-saga/effects';
import { handleGetIndex } from '../../../logic/index';

export function handleMovePiece({ payload }) {
  const { board, square } = payload;

  console.log(handleGetIndex({ square }));
}

export default all([takeLatest('@board/HANDLE_MOVE_PIECE', handleMovePiece)]);
