import React from 'react';
import { useSelector } from 'react-redux';
import { handleGetIndex } from '../logic';
import { handleMovePiece } from '../store/modules/board/actions';

import { Container, Board, Square, Piece } from './styles';

export default function Page() {
  const board = useSelector((state) => state.board.board);

  function handlePieceClick({ square }) {}

  return (
    <Container>
      <Board>
        {board.map((row) =>
          row.map((square) => (
            <Square key={row.indexOf(square)} color={square.color}>
              {square.piece && (
                <Piece
                  onClick={() => handlePieceClick({ square })}
                  key={row.indexOf(square)}
                  color={square.piece}
                />
              )}
            </Square>
          ))
        )}
      </Board>
    </Container>
  );
}
